package javaOops;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 0;
		while (i < 10) {
			System.out.println(i);
			i++;
		}

		int j = 0;
		while (j <= 10) {
			System.out.println(j);
			j++;
		}

		int k = 10;
		while (k > 0) {
			System.out.println(k);
			k--;
		}
		int m = 10;
		while (m >= 0) {
			System.out.println(m);
			m--;
		}
	}

}
